T = int(input())for t in range(T):    N = int(input())    A = list(map(int, input().split()))    B = list(map(int, input().split()))    A.sort()    B.sort()        A_t = {}    B_t = {}    times ={}        min_value = min(A[0], B[0])        for i in range(N):        if times.get(A[i]) == None:            times[A[i]] = 1        else:            times[A[i]] += 1                if times.get(B[i]) == None:            times[B[i]] = 1        else:            times[B[i]] += 1        flag = 0    for i in times:        if times[i] % 2 != 0:            flag = 1            break        else:            A_t[i] = int(times[i]/2)        if flag == 1:        print (-1)        else:        v1 = []        v2 = []                B_t = A_t.copy()        for i in range(N):            if A_t[A[i]] != 0:                A_t[A[i]] -= 1            else:                v1.append(A[i])                            if B_t[B[i]] != 0:                B_t[B[i]] -= 1            else:                v2.append(B[i])                        if len(v1) == 0:            print (0)                    else:            v1.sort()            v2.sort()            cost = 0            for i in range(len(v1)):                cost += min(2 * min_value, min(v1[i], v2[i]))            print (cost)